            {/*                    type="text"*/}
            {/*                    value={monsterName}*/}
            {/*                    onChange={(e) => setMonsterName(e.target.value)}*/}
            {/*                />*/}
            {/*            </label>*/}
            {/*        </div>*/}
            {/*        <div>*/}
            {/*            <label>*/}
            {/*                Blood Level:*/}
            {/*                <input*/}
            {/*                    type="number"*/}
            {/*                    value={bloodLevel}*/}
            {/*                    onChange={(e) => setBloodLevel(e.target.value)}*/}
            {/*                />*/}
            {/*            </label>*/}
            {/*        </div>*/}
            {/*        <button type="submit">Create Game</button>*/}
            {/*    </form>*/}
            {/*</Modal>*/}
            <footer>
                <p>&copy; 2024 Monster Fighting App</p>
            </footer>
        </div>
    );
};

export default CreateGame;
